pub enum AddressError<T> where T: BitStore {

    /// `Address` cannot use the null pointer.
    Null,

    /// `Address` cannot be misaligned for the referent type `T`.
    Misaligned(*const T),
}

pub enum BitPtrError<T> where T: BitStore {

    /// The element address was somehow invalid.
    InvalidAddress(AddressError<T>),

    /// The bit index was somehow invalid.
    InvalidIndex(BitIdxError<T::Mem>),
}

pub enum BitSpanError<T> where T: BitStore {

    /// The base `BitPtr` is invalid.
    InvalidBitptr(BitPtrError<T>),

    /// `BitSpan` domains have a length ceiling.
    TooLong(usize),

    /// `BitSpan` domains have an address ceiling.
    TooHigh(*const T),
}

