pub enum TypedEncodedError {

    /// Error upon decoding.
    Decode(scale::Error),

    /// When operating on instances with different types.
    #[from(ignore)]
    DifferentTypes {
        lhs: core::any::TypeId,
        rhs: core::any::TypeId,
    },

    /// When an already initialized instance is about to be initialized.
    #[from(ignore)]
    AlreadyInitialized {
        initialized_id: core::any::TypeId,
        new_id: core::any::TypeId,
    },

    /// When operating on still uninitialized types.
    #[from(ignore)]
    StillUninitialized,
}

pub enum Error {

    /// Error upon decoding an encoded value.
    Decode(scale::Error),

    /// An error that can only occure in the off-chain environment.
    #[cfg(any(feature = "std", test, doc))]
    OffChain(OffChainError),

    /// The call to another contract has trapped.
    CalleeTrapped,

    /// The call to another contract has been reverted.
    CalleeReverted,

    /// The queried contract storage entry is missing.
    KeyNotFound,

    /// Transfer failed because it would have brought the sender's total balance
    /// below the subsistence threshold.
    BelowSubsistenceThreshold,

    /// Transfer failed for other not further specified reason. Most probably
    /// reserved or locked balance of the sender that was preventing the transfer.
    TransferFailed,

    /// The newly created contract is below the subsistence threshold after executing
    /// its constructor so no usable contract instance will be created.
    NewContractNotFunded,

    /// No code could be found at the supplied code hash.
    CodeNotFound,

    /// The account that was called is either no contract (e.g. user account) or is a tombstone.
    NotCallable,

    /// An unknown error has occured.
    UnknownError,
}
