pub enum Item {

    /// The item is an ink! specific item.
    Ink(InkItem),

    /// The item is a normal Rust item.
    Rust(syn::Item),
}

pub enum InkItem {

    /// The ink! storage struct definition.
    Storage(ir::Storage),

    /// An ink! event definition.
    Event(ir::Event),

    /// An ink! implementation block.
    ImplBlock(ir::ItemImpl),
}

pub enum CallableKind {

    /// An ink! message externally callable.
    Message,

    /// An ink! constructor externally callable.
    Constructor,
}

pub enum ImplItem {

    /// A `#[ink(constructor)]` marked inherent function.
    Constructor(Constructor),

    /// A `#[ink(message)]` marked method.
    Message(Message),

    /// Any other implementation block item.
    Other(syn::ImplItem),
}

pub enum Receiver {

    /// The `&self` message receiver.
    Ref,

    /// The `&mut self` message receiver.
    RefMut,
}
